import React from 'react';
import { useTheme } from '../context/ThemeContext';
import { useLiteMode } from '../context/LiteModeContext';
import { motion } from 'framer-motion';

const AmbientBackground = () => {
    const { theme } = useTheme();
    const { isLiteMode } = useLiteMode();
    const [isMobile, setIsMobile] = React.useState(false);

    React.useEffect(() => {
        const checkMobile = () => setIsMobile(window.innerWidth < 768);
        checkMobile();
        window.addEventListener('resize', checkMobile);
        return () => window.removeEventListener('resize', checkMobile);
    }, []);

    return (
        <div className="fixed inset-0 z-0 pointer-events-none overflow-hidden bg-primary transition-colors duration-500">
            {/* Background Effects */}
            {(isLiteMode || isMobile) ? (
                /* Mobile/Lite Mode: Static subtle gradient (Ultra-Performance) */
                <div className="absolute inset-0 bg-gradient-to-tr from-accent/5 via-transparent to-blue-900/5" />
            ) : (
                /* High-End Mode: Animated Orbs */
                <>
                    <motion.div
                        animate={{
                            x: [0, 50, 0],
                            y: [0, 30, 0],
                            scale: [1, 1.1, 1],
                        }}
                        transition={{
                            duration: 15,
                            repeat: Infinity,
                            ease: "linear"
                        }}
                        className="absolute top-[-20%] left-[-10%] w-[50vw] h-[50vw] bg-accent/5 rounded-full blur-[80px] will-change-transform"
                    />
                    <motion.div
                        animate={{
                            x: [0, -50, 0],
                            y: [0, -30, 0],
                            scale: [1, 1.2, 1],
                        }}
                        transition={{
                            duration: 18,
                            repeat: Infinity,
                            ease: "linear"
                        }}
                        className="absolute bottom-[-20%] right-[-10%] w-[60vw] h-[60vw] bg-blue-900/5 rounded-full blur-[100px] will-change-transform"
                    />
                </>
            )}

            {/* Floating Particles (Stardust) - Reduced Count on mobile */}
            <div className="absolute inset-0 opacity-20">
                {[...Array((isLiteMode || isMobile) ? 4 : 20)].map((_, i) => ( // Minimal particles for mobile
                    <motion.div
                        key={i}
                        initial={{
                            x: Math.random() * window.innerWidth,
                            y: Math.random() * window.innerHeight,
                        }}
                        animate={{
                            y: [0, -50],
                            opacity: [0, 0.8, 0],
                        }}
                        transition={{
                            duration: Math.random() * 10 + 15,
                            repeat: Infinity,
                            ease: "linear",
                            delay: Math.random() * 5
                        }}
                        className={`absolute w-1 h-1 bg-white rounded-full will-change-transform ${isLiteMode ? '' : 'blur-xl'}`}
                    />
                ))}
            </div>

            {/* Noise Overlay for Texture - Optimized opacity */}
            <div className="absolute inset-0 opacity-[0.02] pointer-events-none bg-[url('https://grainy-gradients.vercel.app/noise.svg')]"></div>
        </div>
    );
};

export default AmbientBackground;

import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { X, Plane, Calendar, Users, ArrowRight } from 'lucide-react';
import { useTheme } from '../context/ThemeContext';

const BookingModal = ({ isOpen, onClose }) => {
    const [step, setStep] = useState(1);
    const [loading, setLoading] = useState(false);
    const { theme } = useTheme();

    if (!isOpen) return null;

    const handleSearch = (e) => {
        e.preventDefault();
        setLoading(true);
        setTimeout(() => {
            setLoading(false);
            setStep(2);
        }, 1500);
    };

    const isDark = theme === 'dark';

    return (
        <AnimatePresence>
            <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                className="fixed inset-0 z-[60] flex items-center justify-center p-4 bg-black/60 backdrop-blur-sm"
            >
                <motion.div
                    initial={{ scale: 0.95, y: 20 }}
                    animate={{ scale: 1, y: 0 }}
                    className={`${isDark ? 'bg-[#1a1e21] border-white/10' : 'bg-white border-gray-200'} w-full max-w-lg rounded-2xl border overflow-hidden shadow-2xl transition-colors duration-300`}
                >
                    <div className={`${isDark ? 'bg-white/5 border-white/5' : 'bg-gray-50 border-gray-100'} p-6 border-b flex justify-between items-center transaction-colors duration-300`}>
                        <h3 className={`text-xl font-serif ${isDark ? 'text-white' : 'text-gray-900'}`}>Book Your Flight</h3>
                        <button onClick={onClose} className={`${isDark ? 'text-gray-400 hover:text-white' : 'text-gray-500 hover:text-gray-900'} transition-colors`}>
                            <X size={20} />
                        </button>
                    </div>

                    <div className="p-6 md:p-8">
                        {step === 1 ? (
                            <form onSubmit={handleSearch} className="space-y-6">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                    <div className="space-y-2">
                                        <label className="text-xs text-accent uppercase tracking-wider">From</label>
                                        <div className="relative group">
                                            <Plane className="absolute left-3 top-3 text-gray-400 group-focus-within:text-accent w-4 h-4" />
                                            <select className={`w-full ${isDark ? 'bg-black/20 border-white/10 text-white' : 'bg-gray-50 border-gray-200 text-gray-900'} border rounded-lg py-2.5 pl-10 pr-4 focus:outline-none focus:border-accent appearance-none transition-colors duration-300`}>
                                                <option>Karachi (KHI)</option>
                                                <option>Lahore (LHE)</option>
                                                <option>Islamabad (ISB)</option>
                                            </select>
                                        </div>
                                    </div>
                                    <div className="space-y-2">
                                        <label className="text-xs text-accent uppercase tracking-wider">To</label>
                                        <div className="relative group">
                                            <Plane className="absolute left-3 top-3 text-gray-400 group-focus-within:text-accent w-4 h-4 transform rotate-90" />
                                            <select className={`w-full ${isDark ? 'bg-black/20 border-white/10 text-white' : 'bg-gray-50 border-gray-200 text-gray-900'} border rounded-lg py-2.5 pl-10 pr-4 focus:outline-none focus:border-accent appearance-none transition-colors duration-300`}>
                                                <option>Dubai (DXB)</option>
                                                <option>Jeddah (JED)</option>
                                                <option>London (LHR)</option>
                                            </select>
                                        </div>
                                    </div>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                    <div className="space-y-2">
                                        <label className="text-xs text-accent uppercase tracking-wider">Departure</label>
                                        <div className="relative">
                                            <Calendar className="absolute left-3 top-3 text-gray-400 w-4 h-4" />
                                            <input type="date" className={`w-full ${isDark ? 'bg-black/20 border-white/10 text-white' : 'bg-gray-50 border-gray-200 text-gray-900'} border rounded-lg py-2.5 pl-10 pr-4 focus:outline-none focus:border-accent transition-colors duration-300`} />
                                        </div>
                                    </div>
                                    <div className="space-y-2">
                                        <label className="text-xs text-accent uppercase tracking-wider">Passengers</label>
                                        <div className="relative">
                                            <Users className="absolute left-3 top-3 text-gray-400 w-4 h-4" />
                                            <select className={`w-full ${isDark ? 'bg-black/20 border-white/10 text-white' : 'bg-gray-50 border-gray-200 text-gray-900'} border rounded-lg py-2.5 pl-10 pr-4 focus:outline-none focus:border-accent appearance-none transition-colors duration-300`}>
                                                <option>1 Adult</option>
                                                <option>2 Adults</option>
                                                <option>Family (2A + 2C)</option>
                                            </select>
                                        </div>
                                    </div>
                                </div>

                                <button
                                    type="submit"
                                    disabled={loading}
                                    className="w-full bg-accent hover:bg-accent/90 text-black font-bold py-3 rounded-xl transition-all flex justify-center items-center gap-2"
                                >
                                    {loading ? (
                                        <span className="w-5 h-5 border-2 border-black/30 border-t-black rounded-full animate-spin" />
                                    ) : (
                                        <>Search Flights <ArrowRight size={18} /></>
                                    )}
                                </button>
                            </form>
                        ) : (
                            <div className="text-center py-8">
                                <div className="w-16 h-16 bg-accent/10 rounded-full flex items-center justify-center mx-auto mb-4 text-accent">
                                    <Plane size={32} />
                                </div>
                                <h4 className={`text-xl font-serif mb-2 ${isDark ? 'text-white' : 'text-gray-900'}`}>Prototype Mode</h4>
                                <p className="text-gray-400 mb-6">Real flight data integration would happen here.</p>
                                <button
                                    onClick={() => { setStep(1); onClose(); }}
                                    className="text-sm text-accent hover:underline"
                                >
                                    Close Preview
                                </button>
                            </div>
                        )}
                    </div>
                </motion.div>
            </motion.div>
        </AnimatePresence>
    );
};

export default BookingModal;

import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

const Cursor = () => {
    const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
    const [isHovering, setIsHovering] = useState(false);

    useEffect(() => {
        const mouseMove = (e) => {
            setMousePosition({
                x: e.clientX,
                y: e.clientY
            });
        };

        const handleMouseOver = (e) => {
            if (e.target.tagName === 'BUTTON' || e.target.tagName === 'A' || e.target.closest('button') || e.target.closest('a')) {
                setIsHovering(true);
            } else {
                setIsHovering(false);
            }
        };

        window.addEventListener("mousemove", mouseMove);
        window.addEventListener("mouseover", handleMouseOver);

        return () => {
            window.removeEventListener("mousemove", mouseMove);
            window.removeEventListener("mouseover", handleMouseOver);
        };
    }, []);

    const variants = {
        default: {
            x: mousePosition.x - 16,
            y: mousePosition.y - 16,
            height: 32,
            width: 32,
            backgroundColor: "transparent",
            border: "1px solid #C0985A",
        },
        hover: {
            x: mousePosition.x - 32,
            y: mousePosition.y - 32,
            height: 64,
            width: 64,
            backgroundColor: "rgba(192, 152, 90, 0.1)",
            border: "1px solid #C0985A",
        }
    };

    const dotVariants = {
        default: {
            x: mousePosition.x - 4,
            y: mousePosition.y - 4,
            height: 8,
            width: 8,
            backgroundColor: "#C0985A"
        },
        hover: {
            x: mousePosition.x - 4,
            y: mousePosition.y - 4,
            height: 8,
            width: 8,
            backgroundColor: "#C0985A"
        }
    };

    return (
        <>
            {/* Outer Ring */}
            <motion.div
                className="fixed top-0 left-0 rounded-full pointer-events-none z-[9999] mix-blend-difference hidden md:block"
                variants={variants}
                animate={isHovering ? "hover" : "default"}
                transition={{ type: "spring", stiffness: 500, damping: 28, mass: 0.5 }}
            />
            {/* Inner Dot */}
            <motion.div
                className="fixed top-0 left-0 rounded-full pointer-events-none z-[9999] mix-blend-difference hidden md:block"
                variants={dotVariants}
                animate={isHovering ? "hover" : "default"}
                transition={{ type: "spring", stiffness: 1000, damping: 50 }} // Faster follow
            />
        </>
    );
};

export default Cursor;

import React, { useRef, useState } from 'react';
import { motion } from 'framer-motion';

const MagneticButton = ({ children, className, onClick }) => {
    const ref = useRef(null);
    const [position, setPosition] = useState({ x: 0, y: 0 });

    const handleMouse = (e) => {
        const { clientX, clientY } = e;
        const { height, width, left, top } = ref.current.getBoundingClientRect();
        const middleX = clientX - (left + width / 2);
        const middleY = clientY - (top + height / 2);
        setPosition({ x: middleX * 0.2, y: middleY * 0.2 }); // Movement strength
    };

    const reset = () => {
        setPosition({ x: 0, y: 0 });
    };

    return (
        <motion.button
            ref={ref}
            animate={{ x: position.x, y: position.y }}
            transition={{ type: "spring", stiffness: 150, damping: 15, mass: 0.1 }}
            onMouseMove={handleMouse}
            onMouseLeave={reset}
            onClick={onClick}
            className={className}
        >
            {children}
        </motion.button>
    );
};

export default MagneticButton;

import React, { useState } from 'react';
import { ASSETS } from '../constants';
import { Menu, X, ChevronDown, ChevronUp } from 'lucide-react';
import { Link, useNavigate } from 'react-router-dom';
import { useTheme } from '../context/ThemeContext';
import { useLiteMode } from '../context/LiteModeContext';

const Navbar = ({ onBookClick }) => {
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [activeDropdown, setActiveDropdown] = useState(null); // Desktop hover state
    const [activeMobileDropdown, setActiveMobileDropdown] = useState(null); // Mobile click state
    const navigate = useNavigate();
    const { theme } = useTheme();
    const { isLiteMode } = useLiteMode();

    const handleLogoClick = () => {
        navigate('/');
        window.scrollTo(0, 0);
    }

    const toggleMobileDropdown = (name) => {
        if (activeMobileDropdown === name) {
            setActiveMobileDropdown(null);
        } else {
            setActiveMobileDropdown(name);
        }
    };

    const navLinks = [
        {
            name: 'About Us',
            path: '/about',
            dropdown: [
                { label: 'Introduction', path: '/about#introduction' },
                { label: 'Vision & Mission', path: '/about#vision' },
                { label: 'Our Fleet', path: '/about#fleet' },
                { label: 'Management', path: '/about#management' },
                { label: 'Our Team', path: '/about#team' }
            ]
        },
        {
            name: 'Services',
            path: '/services',
            dropdown: [
                { label: 'Cargo', path: '/services#cargo' },
                { label: 'Flight Schedule', path: '/services#schedule' },
                { label: 'Baggage', path: '/services#baggage' },
                { label: 'Lounges', path: '/services#lounges' },
                { label: 'Special Needs', path: '/services#special-needs' }
            ]
        },
        { name: 'Destinations', path: '/destinations' },
        {
            name: 'Premium',
            path: '/loyalty', // Default to Loyalty
            dropdown: [
                { label: 'Sial Miles', path: '/loyalty' },
                { label: 'My Booking', path: '/manage-booking' },
                { label: 'Flight Status', path: '/flight-status' },
                { label: 'Agency Enrollment', path: '/agency-enrollment' }
            ]
        },
        { name: 'Contact', path: '/contact' },
        { name: 'Feedback', path: '/feedback' },
    ];

    return (
        <nav className="absolute top-0 left-0 w-full px-6 py-5 md:p-8 z-[100] pointer-events-none">
            <div className="flex w-full items-center justify-between relative pt-4 md:pt-0">
                {/* Left: Logo */}
                <div className="flex-1 flex justify-start z-50 pointer-events-auto">
                    <Link to="/" onClick={() => window.scrollTo(0, 0)} className="block">
                        <img src={ASSETS.brandLogo} alt="AirSial" className="h-6 sm:h-8 md:h-10 w-auto object-contain drop-shadow-lg" />
                    </Link>
                </div>

                {/* Center: Navigation Links */}
                <div className="hidden xl:flex flex-1 justify-center gap-3 xl:gap-6 pointer-events-auto">
                    {navLinks.map((link) => (
                        <div key={link.name} className="relative group shrink-0" onMouseEnter={() => setActiveDropdown(link.name)} onMouseLeave={() => setActiveDropdown(null)}>
                            <Link to={link.path} className="text-white hover:text-accent transition-colors text-xs xl:text-xs uppercase tracking-widest font-medium py-2 block whitespace-nowrap">
                                {link.name}
                            </Link>

                            {link.dropdown && (
                                <div className="absolute top-full left-0 pt-2 w-48 opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-300 transform group-hover:translate-y-0 translate-y-2">
                                    <div className={`${isLiteMode ? 'bg-black/95' : 'bg-black/90 backdrop-blur-md'} border border-white/10 rounded-lg p-2 shadow-xl`}>
                                        {link.dropdown.map((item) => (
                                            <Link key={item.label} to={item.path} className="block px-4 py-2 text-gray-100 hover:text-white hover:bg-white/10 rounded text-xs uppercase tracking-wider transition-colors">
                                                {item.label}
                                            </Link>
                                        ))}
                                    </div>
                                </div>
                            )}
                        </div>
                    ))}
                </div>

                {/* Right: Actions Container */}
                <div className="flex-1 flex gap-4 items-center justify-end z-[100] pointer-events-auto">
                    {/* Mobile Actions */}
                    <div className="xl:hidden flex items-center gap-3">
                        <button onClick={onBookClick} className="px-4 py-1.5 text-[10px] uppercase font-bold tracking-widest bg-[#C0985A]/20 border border-[#C0985A]/50 text-[#C0985A] rounded-full backdrop-blur-md flex items-center justify-center whitespace-nowrap">
                            Book Now
                        </button>
                        <button onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)} className="text-white p-1 hover:text-accent transition-colors shrink-0">
                            {isMobileMenuOpen ? <X size={28} /> : <Menu size={28} />}
                        </button>
                    </div>

                    {/* Desktop Actions */}
                    <div className="hidden xl:flex items-center gap-4">
                        {/* Live Status Pill */}
                        <Link to="/flight-status" className="flex shrink-0 items-center gap-2 px-4 py-1.5 backdrop-blur-md bg-white/10 border border-white/20 text-white text-[11px] uppercase tracking-widest font-bold rounded-full hover:bg-white/20 transition-all cursor-pointer">
                            <div className="shrink-0 w-2 h-2 rounded-full bg-green-500 animate-pulse"></div>
                            <span className="whitespace-nowrap">PF-121 to LHE • On Time</span>
                        </Link>

                        {/* Book Flight */}
                        <button onClick={onBookClick} className="shrink-0 whitespace-nowrap px-6 py-2 backdrop-blur-md bg-white/10 border border-white/20 text-white text-[11px] uppercase tracking-widest font-bold rounded-full hover:bg-white/20 transition-all">
                            Book Flight
                        </button>
                    </div>
                </div>
            </div>

            {/* Mobile Menu Overlay */}
            {isMobileMenuOpen && (
                <div className={`xl:hidden fixed inset-0 z-40 ${isLiteMode ? 'bg-black' : 'bg-black/95 backdrop-blur-xl'} pt-24 px-6 flex flex-col gap-4 overflow-y-auto animate-in fade-in slide-in-from-top-5 pointer-events-auto`}>
                    {/* Note: Redundant X button removed as per user request */}

                    {navLinks.map((link) => (
                        <div key={link.name} className="border-b border-white/10 pb-2">
                            {link.dropdown ? (
                                <div>
                                    <button
                                        onClick={() => toggleMobileDropdown(link.name)}
                                        className="w-full flex justify-between items-center text-white text-xl font-serif hover:text-accent transition-colors py-2"
                                    >
                                        <span>{link.name}</span>
                                        {activeMobileDropdown === link.name ? (
                                            <ChevronUp size={20} className="text-accent" />
                                        ) : (
                                            <ChevronDown size={20} className="text-white/50" />
                                        )}
                                    </button>

                                    {/* Collapsible Content */}
                                    <div className={`overflow-hidden transition-all duration-300 ${activeMobileDropdown === link.name ? 'max-h-96 opacity-100 mt-2' : 'max-h-0 opacity-0'}`}>
                                        <div className="pl-4 space-y-3 pb-2 border-l border-white/10 ml-2">
                                            {link.dropdown.map((item) => (
                                                <Link
                                                    key={item.label}
                                                    to={item.path}
                                                    className="block text-gray-300 text-sm hover:text-white transition-colors"
                                                    onClick={() => setIsMobileMenuOpen(false)}
                                                >
                                                    {item.label}
                                                </Link>
                                            ))}
                                        </div>
                                    </div>
                                </div>
                            ) : (
                                <Link
                                    to={link.path}
                                    className="text-white text-xl font-serif hover:text-accent transition-colors block py-2"
                                    onClick={() => setIsMobileMenuOpen(false)}
                                >
                                    {link.name}
                                </Link>
                            )}
                        </div>
                    ))}

                    {/* Mobile Live Status Pill */}
                    <Link
                        to="/flight-status"
                        onClick={() => setIsMobileMenuOpen(false)}
                        className="mt-4 flex items-center justify-center gap-3 px-4 py-3 rounded-full backdrop-blur-xl bg-[#111518]/60 border border-white/10 shadow-lg active:bg-[#111518]/80 transition-colors w-full"
                    >
                        <div className="shrink-0 w-2.5 h-2.5 rounded-full bg-green-500 animate-pulse"></div>
                        <span className="text-xs text-white/90 tracking-widest uppercase font-medium whitespace-nowrap">
                            PF-121 to LHE • On Time
                        </span>
                    </Link>

                    <button
                        onClick={() => {
                            onBookClick();
                            setIsMobileMenuOpen(false);
                        }}
                        className="w-full py-4 bg-accent text-black font-bold text-lg rounded-full mt-4 mb-4"
                    >
                        Book Flight
                    </button>

                </div>
            )}
        </nav>
    );
};

export default Navbar;

import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { ASSETS } from '../constants';

const Preloader = ({ onComplete }) => {
    const [textIndex, setTextIndex] = useState(0);
    const words = ["Comfort", "Luxury", "Elegance", "AirSial"];

    useEffect(() => {
        const interval = setInterval(() => {
            setTextIndex(prev => {
                if (prev === words.length - 1) {
                    clearInterval(interval);
                    setTimeout(onComplete, 1500); // Extended wait for final logo
                    return prev;
                }
                return prev + 1;
            });
        }, 500); // Slower for premium feel

        return () => clearInterval(interval);
    }, [onComplete, words.length]);

    return (
        <motion.div
            initial={{ y: 0 }}
            exit={{ y: "-100%", transition: { duration: 0.8, ease: [0.76, 0, 0.24, 1] } }}
            className="fixed inset-0 z-[100] bg-black flex items-center justify-center overflow-hidden"
        >
            <div className="flex flex-col items-center">
                <div className="h-24 flex items-center justify-center overflow-hidden">
                    <AnimatePresence mode="wait">
                        <motion.div
                            key={textIndex}
                            initial={{ opacity: 0, y: 40 }}
                            animate={{ opacity: 1, y: 0 }}
                            exit={{ opacity: 0, y: -40 }}
                            transition={{ duration: 0.3, ease: "easeOut" }}
                            className="text-white text-4xl md:text-6xl font-serif tracking-widest uppercase text-center"
                        >
                            {words[textIndex]}
                        </motion.div>
                    </AnimatePresence>
                </div>

                {/* Progress Bar */}
                <motion.div
                    initial={{ width: 0 }}
                    animate={{ width: "200px" }}
                    transition={{ duration: 2, ease: "easeInOut" }}
                    className="mt-8 h-0.5 bg-accent"
                />
            </div>
        </motion.div>
    );
};

export default Preloader;

import React from 'react';
import { motion } from 'framer-motion';

const TextReveal = ({ children, className }) => {
    // Split text into words
    const words = children.split(" ");

    const container = {
        hidden: { opacity: 0 },
        visible: (i = 1) => ({
            opacity: 1,
            transition: { staggerChildren: 0.12, delayChildren: 0.04 * i },
        }),
    };

    const child = {
        visible: {
            opacity: 1,
            y: 0,
            transition: {
                type: "spring",
                damping: 12,
                stiffness: 100,
            },
        },
        hidden: {
            opacity: 0,
            y: 20,
            transition: {
                type: "spring",
                damping: 12,
                stiffness: 100,
            },
        },
    };

    return (
        <motion.div
            variants={container}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
            className={`flex flex-wrap overflow-hidden ${className}`}
        >
            {words.map((word, index) => (
                <motion.span variants={child} key={index} className="pb-1 relative" style={{ willChange: "transform, opacity" }}>
                    {word}
                </motion.span>
            ))}
        </motion.div>
    );
};

export default TextReveal;

import React from 'react';
import { motion } from 'framer-motion';
import { Sun, Moon } from 'lucide-react';
import { useTheme } from '../context/ThemeContext';
import { useLiteMode } from '../context/LiteModeContext';

const ThemeToggle = () => {
    const { theme, toggleTheme } = useTheme();
    const { isLiteMode } = useLiteMode();

    return (
        <div className={`relative flex items-center p-0.5 rounded-full border border-white/20 ml-2 md:ml-4 ${isLiteMode ? 'bg-black/10' : 'bg-white/5 backdrop-blur-md'}`}>
            {/* Sliding "Liquid" Background */}
            <motion.div
                layout
                className="absolute top-0.5 bottom-0.5 w-[calc(50%-2px)] bg-white/20 rounded-full shadow-inner z-0"
                initial={false}
                animate={{
                    x: theme === 'green' ? '100%' : '0%'
                }}
                transition={{
                    type: "spring",
                    stiffness: 300,
                    damping: 30
                }}
            />

            {/* Dark Mode Button (Moon) - Left */}
            <button
                onClick={() => theme === 'green' && toggleTheme()}
                className={`relative z-10 p-1.5 rounded-full transition-colors flex items-center justify-center w-7 h-7 ${theme === 'dark' ? 'text-white' : 'text-white/50 hover:text-white/80'}`}
                aria-label="Switch to Dark Mode"
            >
                <Moon size={14} />
            </button>

            {/* Light/Green Mode Button (Sun) - Right */}
            <button
                onClick={() => theme === 'dark' && toggleTheme()}
                className={`relative z-10 p-1.5 rounded-full transition-colors flex items-center justify-center w-7 h-7 ${theme === 'green' ? 'text-accent' : 'text-white/50 hover:text-white/80'}`}
                aria-label="Switch to Light Mode"
            >
                <Sun size={14} />
            </button>
        </div>
    );
};

export default ThemeToggle;

import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { ArrowLeft, ArrowRight, Tag } from 'lucide-react';
import { ASSETS } from '../../constants';
import TextReveal from '../TextReveal';

const DestinationsSection = () => {
    // Reordered so Islamabad is index 2 (Center if 5 items, or just default to it)
    const destinations = [
        { id: 'khi', img: ASSETS.destKarachi, code: 'KHI', city: 'Karachi', price: 'PKR 18,500', desc: 'The bustling metropolis and economic hub of Pakistan, offering a vibrant mix of culture and commerce.' },
        { id: 'lhe', img: ASSETS.destLahore, code: 'LHE', city: 'Lahore', price: 'PKR 22,000', desc: 'The cultural heart of Pakistan, known for its rich history, stunning Mughal architecture, and delicious food.' },
        { id: 'isb', img: ASSETS.destIslamabad, code: 'ISB', city: 'Islamabad', price: 'PKR 24,000', desc: 'The serene capital city, nestled against the Margalla Hills, famous for its lush greenery and peaceful environment.' },
        { id: 'skt', img: ASSETS.destSialkot, code: 'SKT', city: 'Sialkot', price: 'PKR 20,000', desc: 'A major industrial city, renowned globally for its sports goods and surgical instruments manufacturing.' },
        { id: 'jed', img: ASSETS.destJeddah, code: 'JED', city: 'Jeddah', price: 'PKR 85,000', desc: 'A vibrant Saudi Arabian port city on the Red Sea, serving as a gateway for pilgrims to Mecca and Medina.' },
        { id: 'mct', img: ASSETS.destMuscat, code: 'MCT', city: 'Muscat', price: 'PKR 45,000', desc: 'Oman\'s port capital, known for its stunning architecture, traditional souqs, and beautiful coastline.' },
    ];

    const [activeIndex, setActiveIndex] = useState(2); // Start at Islamabad (Index 2)

    const handleNext = () => {
        setActiveIndex((prev) => (prev + 1 < destinations.length ? prev + 1 : 0));
    };

    const handlePrev = () => {
        setActiveIndex((prev) => (prev - 1 >= 0 ? prev - 1 : destinations.length - 1));
    };

    return (
        <section className="py-12 md:py-24 bg-transparent overflow-hidden relative">
            <div className="mb-12 md:mb-24 px-6 md:px-20 flex items-end justify-between relative z-10">
                <div>
                    <TextReveal className="text-4xl md:text-6xl text-accent mb-6 gap-x-3">
                        Global Destinations
                    </TextReveal>
                    <p className="text-gray-100 text-lg">Connecting Pakistan to the Middle East and beyond.</p>
                </div>

                {/* Desktop Controls */}
                <div className="hidden md:flex gap-4">
                    <button onClick={handlePrev} className="p-4 rounded-full border border-white/20 text-white hover:bg-white/10 transition-colors z-20 pointer-events-auto">
                        <ArrowLeft size={24} />
                    </button>
                    <button onClick={handleNext} className="p-4 rounded-full border border-white/20 text-white hover:bg-white/10 transition-colors z-20 pointer-events-auto">
                        <ArrowRight size={24} />
                    </button>
                </div>
            </div>

            {/* 3D Coverflow Container */}
            <div className="relative h-[400px] md:h-[500px] flex justify-center items-center perspective-1000">
                <AnimatePresence initial={false} mode='popLayout'>
                    {destinations.map((dest, index) => {
                        const offset = index - activeIndex;
                        const isActive = index === activeIndex;

                        return (
                            <motion.div
                                key={dest.id}
                                layout
                                initial={false}
                                animate={{
                                    x: offset * (window.innerWidth < 768 ? 60 : 280), // Increased spacing for landscape cards
                                    scale: isActive ? 1 : 0.85,
                                    opacity: isActive ? 1 : 0.4,
                                    zIndex: 50 - Math.abs(offset),
                                    rotateY: isActive ? 0 : offset * 5, // Subtle rotation
                                }}
                                drag="x"
                                dragConstraints={{ left: 0, right: 0 }}
                                dragElastic={0.1}
                                onDragEnd={(e, { offset, velocity }) => {
                                    const swipe = Math.abs(offset.x) * velocity.x;
                                    if (swipe < -100 || offset.x < -100) {
                                        handleNext();
                                    } else if (swipe > 100 || offset.x > 100) {
                                        handlePrev();
                                    }
                                }}
                                transition={{
                                    type: "spring",
                                    stiffness: 260,
                                    damping: 20
                                }}
                                onClick={() => setActiveIndex(index)}
                                className={`absolute w-[85vw] md:w-[600px] aspect-[4/3] rounded-3xl overflow-hidden shadow-2xl border ${isActive ? 'border-accent/50 box-shadow-accent cursor-grab active:cursor-grabbing' : 'border-white/5 cursor-pointer'}`}
                                style={{
                                    boxShadow: isActive ? '0 25px 50px -12px rgba(0, 0, 0, 0.7)' : 'none'
                                }}
                            >
                                <img src={dest.img} alt={dest.code} className="w-full h-full object-cover pointer-events-none" loading="lazy" decoding="async" />
                                <div className={`absolute inset-0 bg-black/20 transition-opacity duration-300 ${isActive ? 'opacity-0' : 'opacity-60'}`} />

                                {/* Content Overlay - Only visible on active card */}
                                <motion.div
                                    initial={{ opacity: 0, y: 20 }}
                                    animate={{ opacity: isActive ? 1 : 0, y: isActive ? 0 : 20 }}
                                    transition={{ duration: 0.3 }}
                                    className="absolute bottom-0 left-0 w-full p-6 md:p-10 bg-gradient-to-t from-black via-black/80 to-transparent"
                                >
                                    <h3 className="text-2xl md:text-4xl font-serif text-white mb-2">{dest.city}</h3>
                                    <div className="flex gap-4 items-center">
                                        <p className="text-white flex items-center gap-2 text-sm md:text-lg"><Tag size={16} className="text-accent" /> {dest.price}</p>
                                    </div>
                                    <p className="text-white text-xs md:text-sm mt-2 line-clamp-2 leading-relaxed">{dest.desc}</p>
                                </motion.div>
                            </motion.div>
                        );
                    })}
                </AnimatePresence>
            </div>

            {/* Mobile Controls */}
            <div className="flex md:hidden justify-center gap-6 pb-6 pt-0 relative z-20">
                <button onClick={handlePrev} className="p-4 rounded-full bg-white/5 border border-white/10 text-white backdrop-blur-sm active:bg-white/20">
                    <ArrowLeft size={20} />
                </button>
                <div className="w-px h-12 bg-white/10"></div>
                <button onClick={handleNext} className="p-4 rounded-full bg-white/5 border border-white/10 text-white backdrop-blur-sm active:bg-white/20">
                    <ArrowRight size={20} />
                </button>
            </div>
        </section>
    );
};

